package lib

import (
	"encoding/hex"
	"testing"
)

// TestHelloName calls greetings.Hello with a name, checking
// for a valid return value.
func TestLoadTxFromWOC(t *testing.T) {
	network := "main"
	txid := "a9b3fa37e171ffe0e79b507a8a4486f691918a876321e21a85d7a26c8a02fc15"
	rawtx, err := LoadTxFromWOC(txid, network)
	if err != nil {
		t.Fatalf(`LoadTxFromWOC err: %v`, err)
	}

	if hex.EncodeToString(rawtx) != "" {
		t.Fatalf(`The tx not as expected`)
	}
}
